function makeData(x, y, i) {
    //x,y起始值，xRange为x的取值范围；y值不断增长,i生成个数
    var arr1 = [];
    for (var j = 0; j <= i; j++) {
        var arr2 = [];
        x = x + Math.random() * (Math.random() > 0.5 ? 1 : -1);
        x = x > 0 ? x : x + 1;
        x = x < 30 ? x : x - 1;
        y = y + Math.random();
        arr2 = [parseFloat(y.toFixed(4)), parseFloat(x.toFixed(4))];
        arr1.push(arr2);
    }
    return arr1;
}

