function selectVal(arr,num){
    //arr数组元素，num选多少个
    var m=[];
    if(arr.length<=num){
        return arr;
    }else{
        //k为余数
        var k = arr.length % num;
        if(k){
            //没有整除
            //减去余数就能被整除了
            var j=arr.length-k;
            //步进值
            var i=j/num;
            for(var n=0;n<=j;n+i ){
                m.push(arr[n]);
            }
            m.push(arr[j+k]);
            return m;
        }else{
            //整除
            var i=arr.length/num;
            for (var n = 0; n <= arr.length; n + i) {
                m.push(arr[n]);
            }
            return m;
        }
    }
}

